// Function node: parse JSON string in common fields and merge parsed fields into item.json
return items.map(item => {
  // adjust these keys if your JSON string lives under a different field
  const candidateFields = ['output', 'body', 'text', 'data', 'result'];
  let raw = null;
  for (const k of candidateFields) {
    if (item.json && item.json[k]) { raw = item.json[k]; break; }
  }

  if (!raw) return item;

  if (typeof raw === 'string') {
    // remove ```json fences if present
    const cleaned = raw.replace(/```json\s*|```/g, '').trim();
    try {
      const parsed = JSON.parse(cleaned);
      // merge parsed object into item.json
      item.json = Object.assign({}, item.json, parsed);
    } catch (err) {
      // attach parse error so you can debug in n8n UI
      item.json.parseError = err.message;
    }
  }

  return item;
});

