// It will clean escape characters from any dynamic AI output

const input = $json.output || "";  // get AI output dynamically

// Replace escaped characters with actual ones
const cleaned = input
  .replace(/\\n/g, "\n")   // newline
  .replace(/\\t/g, "\t")   // tab
  .replace(/\\"/g, "\"")   // double quotes
  .replace(/\\r/g, "\r");  // carriage return

return {
  cleanedOutput: cleaned
};
